import React, { useState, useEffect } from "react";

const PizzaApp = () => {
  const [orders, setOrders] = useState([]);
  const [totalDelivered, setTotalDelivered] = useState(0);

  const placeOrder = (order) => {
    if (orders.length < 10) {
      setOrders([...orders, { ...order, stage: "Order Placed", time: 0 }]);
    } else {
      alert("Not taking any order for now (Max orders reached)");
    }
  };

  const updateOrderStage = (orderId, newStage) => {
    setOrders((prevOrders) =>
      prevOrders.map((order) =>
        order.id === orderId ? { ...order, stage: newStage, time: 0 } : order
      )
    );
  };

  const cancelOrder = (orderId) => {
    setOrders((prevOrders) => prevOrders.filter((order) => order.id !== orderId));
  };

  useEffect(() => {
    const timer = setInterval(() => {
      setOrders((prevOrders) =>
        prevOrders.map((order) => ({
          ...order,
          time: order.stage === "Order Ready" ? order.time + 1 : order.time,
        }))
      );
    }, 1000);

    return () => clearInterval(timer);
  }, [orders]);

  useEffect(() => {
    const deliveredToday = orders.filter((order) => order.stage === "Delivered").length;
    setTotalDelivered(deliveredToday);
  }, [orders]);

  useEffect(() => {
    // Move from "Order Making" to "Order Ready" after 3 minutes
    const makingToReadyTimer = setTimeout(() => {
      setOrders((prevOrders) =>
        prevOrders.map((order) =>
          order.stage === "Order Making" ? { ...order, stage: "Order Ready" } : order
        )
      );
    }, 1 * 60 * 1000);

    return () => clearTimeout(makingToReadyTimer);
  }, [orders]);

  useEffect(() => {
    // Move from "Order Ready" to "Order Picked" after 3 minutes
    const readyToPickedTimer = setTimeout(() => {
      setOrders((prevOrders) =>
        prevOrders.map((order) =>
          order.stage === "Order Ready" ? { ...order, stage: "Order Picked" } : order
        )
      );
    }, 1 * 60 * 1000);

    return () => clearTimeout(readyToPickedTimer);
  }, [orders]);

  useEffect(() => {
    const timer = setInterval(() => {
      setOrders((prevOrders) =>
        prevOrders.map((order) => ({
          ...order,
          time: order.time + 1,
        }))
      );
    }, 1000);

    return () => clearInterval(timer);
  }, []);
  return (
    <div className="container mt-4">
      <h1 className="text-center">Pizza Shop</h1>
      <PizzaOrderForm placeOrder={placeOrder} />
      <PizzaMainDisplay
        orders={orders}
        updateOrderStage={updateOrderStage}
        cancelOrder={cancelOrder}
      />
      <div className="text-center mt-3">Total Pizza Delivered Today: {totalDelivered}</div>
    </div>
  );
};

const PizzaOrderForm = ({ placeOrder }) => {
  const [order, setOrder] = useState({ type: "", size: "", base: "" });

  const handleChange = (e) => {
    setOrder({ ...order, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (order.type && order.size && order.base) {
      placeOrder({ ...order, id: Date.now(), stage: "Order Making", time: 0 });
      setOrder({ type: "", size: "", base: "" });
    } else {
      alert("Please fill in all fields");
    }
  };

  return (
    <form onSubmit={handleSubmit} className="mb-4 mx-auto" style={{ maxWidth: "400px" }}>
    <div className="row mb-3">
        <label className="form-label">Type:</label>
        <div className="col-sm-10">
            <select
                name="type"
                value={order.type}
                onChange={handleChange}
                className="form-select"
            >
                <option value="">Select</option>
                <option value="Veg">Veg</option>
                <option value="Non-Veg">Non-Veg</option>
            </select>
        </div>
    </div>
    <div className="row mb-3">
        <label className="form-label">Size:</label>
        <div className="col-sm-10">
            <select
                name="size"
                value={order.size}
                onChange={handleChange}
                className="form-select"
            >
                <option value="">Select</option>
                <option value="Large">Large</option>
                <option value="Medium">Medium</option>
                <option value="Small">Small</option>
            </select>
        </div>
    </div>
    <div className="row mb-3">
        <label className="form-label">Base:</label>
        <div className="col-sm-10">
            <select
                name="base"
                value={order.base}
                onChange={handleChange}
                className="form-select"
            >
                <option value="">Select</option>
                <option value="Thin">Thin</option>
                <option value="Thick">Thick</option>
            </select>
        </div>
    </div>
    <button type="submit" className="btn btn-primary">
        Place Order
    </button>
</form>


  );
};

const PizzaMainDisplay = ({ orders, updateOrderStage, cancelOrder }) => {
  return (
    <div>
      <h2 className="text-center mb-3">Main Display</h2>
      <div className="row row-cols-1 row-cols-md-2 g-4">
        {orders.map((order) => (
          <PizzaCard
            key={order.id}
            order={order}
            updateOrderStage={updateOrderStage}
            cancelOrder={cancelOrder}
          />
        ))}
      </div>
    </div>
  );
};

const PizzaCard = ({ order, updateOrderStage, cancelOrder }) => {
  const handleNext = () => {
    if (order.stage === "Order Ready") {
      updateOrderStage(order.id, "Order Picked");
    } else if (order.stage === "Order Making") {
      updateOrderStage(order.id, "Order Ready");
    }
  };

  const handleCancel = () => {
    cancelOrder(order.id);
  };

  return (
    <div className={`col mb-4 ${order.stage === "Delivered" ? "text-white bg-success" : ""}`}>
      <div className="card h-100">
        <div className="card-body">
          <h5 className="card-title">Order ID: {order.id}</h5>
          <p className="card-text">Type: {order.type}</p>
          <p className="card-text">Size: {order.size}</p>
          <p className="card-text">Base: {order.base}</p>
          <p className="card-text">Stage: {order.stage}</p>
          <p className="card-text">Time Spent: {order.time} seconds</p>
          {order.stage !== "Delivered" && (
            <>
              <button onClick={handleNext} className="btn btn-primary me-2">
                Next
              </button>
              <button onClick={handleCancel} className="btn btn-danger">
                Cancel
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default PizzaApp;
