import React, { useState, useEffect } from "react";

const PizzaApp = () => {
  const [orders, setOrders] = useState([]);
  const [totalDelivered, setTotalDelivered] = useState(0);

  const placeOrder = (order) => {
    if (orders.length < 10) {
      setOrders([...orders, { ...order, stage: "Order Placed", time: 0 }]);
    } else {
      alert("Not taking any order for now (Max orders reached)");
    }
  };

  const updateOrderStage = (orderId, newStage) => {
    setOrders((prevOrders) =>
      prevOrders.map((order) =>
        order.id === orderId ? { ...order, stage: newStage } : order
      )
    );
  };

  const cancelOrder = (orderId) => {
    setOrders((prevOrders) => prevOrders.filter((order) => order.id !== orderId));
  };

  useEffect(() => {
    const timer = setInterval(() => {
      setOrders((prevOrders) =>
        prevOrders.map((order) => ({
          ...order,
          time: order.time + 1,
        }))
      );
    }, 1000);

    return () => clearInterval(timer);
  }, []);

  useEffect(() => {
    const deliveredToday = orders.filter((order) => order.stage === "Order Picked").length;
    setTotalDelivered(deliveredToday);
  }, [orders]);

  return (
    <div>
      <h1>Pizza Shop</h1>
      <PizzaOrderForm placeOrder={placeOrder} />
      <PizzaMainDisplay
        orders={orders}
        updateOrderStage={updateOrderStage}
        cancelOrder={cancelOrder}
      />
      <div>Total Pizza Delivered Today: {totalDelivered}</div>
    </div>
  );
};

const PizzaOrderForm = ({ placeOrder }) => {
  const [order, setOrder] = useState({ type: "", size: "", base: "" });

  const handleChange = (e) => {
    setOrder({ ...order, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (order.type && order.size && order.base) {
      placeOrder({ ...order, id: Date.now() });
      setOrder({ type: "", size: "", base: "" });
    } else {
      alert("Please fill in all fields");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Place a Pizza Order</h2>
      <label>
        Type:
        <select name="type" value={order.type} onChange={handleChange}>
          <option value="">Select</option>
          <option value="Veg">Veg</option>
          <option value="Non-Veg">Non-Veg</option>
        </select>
      </label>
      <label>
        Size:
        <select name="size" value={order.size} onChange={handleChange}>
          <option value="">Select</option>
          <option value="Large">Large</option>
          <option value="Medium">Medium</option>
          <option value="Small">Small</option>
        </select>
      </label>
      <label>
        Base:
        <select name="base" value={order.base} onChange={handleChange}>
          <option value="">Select</option>
          <option value="Thin">Thin</option>
          <option value="Thick">Thick</option>
        </select>
      </label>
      <button type="submit">Place Order</button>
    </form>
  );
};

const PizzaMainDisplay = ({ orders, updateOrderStage, cancelOrder }) => {
  return (
    <div>
      <h2>Main Display</h2>
      <div className="pizza-cards">
        {orders.map((order) => (
          <PizzaCard
            key={order.id}
            order={order}
            updateOrderStage={updateOrderStage}
            cancelOrder={cancelOrder}
          />
        ))}
      </div>
    </div>
  );
};

const PizzaCard = ({ order, updateOrderStage, cancelOrder }) => {
  const handleNext = () => {
    updateOrderStage(order.id, "Order in Making");
  };

  const handlePicked = () => {
    updateOrderStage(order.id, "Order Picked");
  };

  const handleCancel = () => {
    cancelOrder(order.id);
  };

  return (
    <div className={`pizza-card ${order.stage === "Order Picked" ? "red-highlight" : ""}`}>
      <h3>Order ID: {order.id}</h3>
      <p>Type: {order.type}</p>
      <p>Size: {order.size}</p>
      <p>Base: {order.base}</p>
      <p>Stage: {order.stage}</p>
      <p>Time Spent: {order.time} seconds</p>
      {order.stage !== "Order Picked" && (
        <>
          <button onClick={handleNext}>Next</button>
          <button onClick={handleCancel}>Cancel</button>
        </>
      )}
      {order.stage === "Order Ready" && <button onClick={handlePicked}>Picked</button>}
    </div>
  );
};

export default PizzaApp;
